INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (408, 1, 'Салат Лоло Бионда', 'Раннеспелый листовой салат предназначенный для выращивания с весны до поздней осени. От полных всходов до технической спелости 40-50 дней. Листья курчавые, светло-зеленой окраски, мясистые и нежные на вкус.', 'Раннеспелый, Поздний, Мясистый', 'Салат Лоло Бионда', 'Раннеспелый листовой салат предназначенный для выращивания с весны до поздней осени. От полных всходов до технической спелости 40-50 дней. Листья курчавые, светло-зеленой окраски, мясистые и нежные на вкус.', 'Раннеспелый, Поздний, Мясистый');
