INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (410, 1, 'Одесский кучерявец', 'Раннеспелый, высокоурожайный (до 5 кг/кв. м) сорт, богатый витаминами и минеральными солями. Растение формирует курчавые листья, без содержания горечи, . культура холодостойкая.', 'Раннеспелый, Холодостойкий, Высокоурожайный', 'Одесский кучерявец', 'Раннеспелый, высокоурожайный (до 5 кг/кв. м) сорт, богатый витаминами и минеральными солями. Растение формирует курчавые листья, без содержания горечи, . культура холодостойкая.', 'Раннеспелый, Холодостойкий, Высокоурожайный');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (411, 1, 'Палитра', 'Смесь семи самых популярных видов салата, 1000семян. Предназначенных для длительного срока сбора в виду разности сроков созревания сортов.', '', 'Палитра', 'Смесь семи самых популярных видов салата, 1000семян. Предназначенных для длительного срока сбора в виду разности сроков созревания сортов.', '');
