INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (414, 1, 'Салат', 'Айсберг. Раннеспелый, высокоурожайный сорт кочанного салата для весеннего и осеннего выращивания в открытом грунте и под пленкой. Вегетационный период от всходов до сбора урожая - 58-65 дней. Формирует крупные кочаны.', 'Раннеспелый, Открытый грунт, Высокоурожайный', 'Салат', 'Айсберг. Раннеспелый, высокоурожайный сорт кочанного салата для весеннего и осеннего выращивания в открытом грунте и под пленкой. Вегетационный период от всходов до сбора урожая - 58-65 дней. Формирует крупные кочаны.', 'Раннеспелый, Открытый грунт, Высокоурожайный');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (415, 1, 'Салат Тайфун', 'Листовой салат с хрустящими сочными листьями. Розетка листьев высотой 20 см, диаметром 27 см. Лист крупный, зеленый, сильноволокнистый. Масса растения 200-220 гр, урожайность 3, 5 кг/м2. Вкус отличный.', 'Сок, Сочный, Хрустящий', 'Салат Тайфун', 'Листовой салат с хрустящими сочными листьями. Розетка листьев высотой 20 см, диаметром 27 см. Лист крупный, зеленый, сильноволокнистый. Масса растения 200-220 гр, урожайность 3, 5 кг/м2. Вкус отличный.', 'Сок, Сочный, Хрустящий');
