INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (1, 2, 'Амурський тигр', 'Високий кущ з щільними червоними плодами з довгими жовтими смужками, тонкою шкіркою з послідовним дозріванням. Стійкий до хвороб.', 'tall,red,yellow,firm,disease_resistant,thin_skin', 'Амурський тигр', 'Високий кущ з щільними червоними плодами з довгими жовтими смужками, тонкою шкіркою з послідовним дозріванням. Стійкий до хвороб.', 'tall,red,yellow,firm,disease_resistant,thin_skin');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (2, 2, 'Апельсин', 'Рослина висотою до 2 метрів, плоди великі жовті, округлої форми, міцні, добре дозрівають, масою до 300 г, сорт для алергіків.', 'yellow,round', 'Апельсин', 'Рослина висотою до 2 метрів, плоди великі жовті, округлої форми, міцні, добре дозрівають, масою до 300 г, сорт для алергіків.', 'yellow,round');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (3, 2, 'Банан оранжевий', 'Ранній урожайний сорт, високорослий з мясистими плодами оранжевого кольору, плоди подовжені з солодким смаком, стійкий до хворіб.', 'early,tall,orange,elongated,sweet,meaty,disease_resistant,high_yield', 'Банан оранжевий', 'Ранній урожайний сорт, високорослий з мясистими плодами оранжевого кольору, плоди подовжені з солодким смаком, стійкий до хворіб.', 'early,tall,orange,elongated,sweet,meaty,disease_resistant,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (4, 2, 'Бичий лоб', 'Середньо-стиглий сорт сибірської селекції індетермінантного типу для відкритого грунту плід округло-ребристий масою до 600гр, салатного типу.', 'mid_maturing,determinate,indeterminate,salad,round,open_field', 'Бичий лоб', 'Середньо-стиглий сорт сибірської селекції індетермінантного типу для відкритого грунту плід округло-ребристий масою до 600гр, салатного типу.', 'mid_maturing,determinate,indeterminate,salad,round,open_field');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (5, 2, 'Бояриня', 'Один з нових сортів, високорослий, середньо-стиглий сорт плоди червоні округлі, придатний для переробки і вживання в свіжому вигляді дуже смачний', 'mid_maturing,tall,fresh,red,round', 'Бояриня', 'Один з нових сортів, високорослий, середньо-стиглий сорт плоди червоні округлі, придатний для переробки і вживання в свіжому вигляді дуже смачний', 'mid_maturing,tall,fresh,red,round');
