INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (32, 2, 'Російський розмір', 'Пізньо-стиглий (130-140) днів, індетермінантний гібрід з необмеженим ростом, формовка в 1 стебло, обовязкова підвязка, Стійкий до вірусів, червон.', 'late,determinate,indeterminate,red,needs_trellis', 'Російський розмір', 'Пізньо-стиглий (130-140) днів, індетермінантний гібрід з необмеженим ростом, формовка в 1 стебло, обовязкова підвязка, Стійкий до вірусів, червон.', 'late,determinate,indeterminate,red,needs_trellis');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (33, 2, 'Сливка гігант (оранжевий)', 'Середньостиглий урожайний сорт подовженного плодоношення, массою 150-180гр, тип  сливка, для консервування та вікористання в свіжім виляд.', 'mid_maturing,canning,fresh,orange,elongated,plum,high_yield,long_fruiting', 'Сливка гігант (оранжевий)', 'Середньостиглий урожайний сорт подовженного плодоношення, массою 150-180гр, тип  сливка, для консервування та вікористання в свіжім виляд.', 'mid_maturing,canning,fresh,orange,elongated,plum,high_yield,long_fruiting');
