INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (268, 2, 'Подарунок Молдови', 'Рослина напіврозлога,низькоросла, висотою 35-45 см. Плоди тьмяні, конусоподібні, довжиною 9-10 см, масою 50-80 г, товщиною стінки 4-5 мм. Смак хороший. Біологічна стиглість темно-червона. Стійкий до хвороб. Універсальне застосування.', 'short,sprawling,universal,red,disease_resistant', 'Подарунок Молдови', 'Рослина напіврозлога,низькоросла, висотою 35-45 см. Плоди тьмяні, конусоподібні, довжиною 9-10 см, масою 50-80 г, товщиною стінки 4-5 мм. Смак хороший. Біологічна стиглість темно-червона. Стійкий до хвороб. Універсальне застосування.', 'short,sprawling,universal,red,disease_resistant');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (269, 2, 'Ратунда', 'Раннє дозрівання. Рослина компактна, низькоросла висотою 25-30 см. Світлолюбна, посухостійка. Плоди округлі, темно-червоні за біологічною стиглості, довжиною близько 6 см, товстостінні (товщина стінки 7-10 мм), масою 102-167 г. Смакові якості відмінні.', 'early_maturing,short,compact,red,round,drought_tolerant', 'Ратунда', 'Раннє дозрівання. Рослина компактна, низькоросла висотою 25-30 см. Світлолюбна, посухостійка. Плоди округлі, темно-червоні за біологічною стиглості, довжиною близько 6 см, товстостінні (товщина стінки 7-10 мм), масою 102-167 г. Смакові якості відмінні.', 'early_maturing,short,compact,red,round,drought_tolerant');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (270, 2, 'Ожаровський', 'Один з кращих польських середньоранніх сортів. Підходить для вирощування в теплицях.  Рослини мають пишний ріст, вимагають пасинкування та підв \'язування.зав\'язь формуеться навіть при слабкому освітленні і низькій температурі. Великі плоди змінюють колір від темно-зеленого до темно-червоного, коли вони дозрівають.', 'mid_early,red,greenhouse', 'Ожаровський', 'Один з кращих польських середньоранніх сортів. Підходить для вирощування в теплицях.  Рослини мають пишний ріст, вимагають пасинкування та підв \'язування.зав\'язь формуеться навіть при слабкому освітленні і низькій температурі. Великі плоди змінюють колір від темно-зеленого до темно-червоного, коли вони дозрівають.', 'mid_early,red,greenhouse');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (271, 2, 'Український гіркий', 'Середньостиглі, плоди конусоподібні, гладкі, червоні, глянцеві. М \'якоть тонка, смак гострий. Застосовується в домашній кухні, для приготування настоянок, олій. Теплолюбна культура, вирощується з використанням розсади.', 'mid_maturing,red', 'Український гіркий', 'Середньостиглі, плоди конусоподібні, гладкі, червоні, глянцеві. М \'якоть тонка, смак гострий. Застосовується в домашній кухні, для приготування настоянок, олій. Теплолюбна культура, вирощується з використанням розсади.', 'mid_maturing,red');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (272, 2, 'Кімнатний', 'У горщиковій культурі перець можна вирощувати протягом 2-3 років до виснаження рослини та отримання свіжих плодів протягом усього року. Посів насіння для кімнатної культури можна проводити в будь-який час року, але найкраще навесні. Взимку рослину слід розміщувати ближче до світла і захищати від холоду.', 'fresh,round', 'Кімнатний', 'У горщиковій культурі перець можна вирощувати протягом 2-3 років до виснаження рослини та отримання свіжих плодів протягом усього року. Посів насіння для кімнатної культури можна проводити в будь-який час року, але найкраще навесні. Взимку рослину слід розміщувати ближче до світла і захищати від холоду.', 'fresh,round');
