INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (275, 2, 'Геліос', 'Середньоранній сорт. Технічна стиглість плодів настає через 100-116 днів після проростання. Рослина компактна, висотою 40-58 см. Плоди округлі, фіолетові, масою 150-200 г. М \'якоть біла, щільна, без гіркоти. Врожайність висока.', 'early,mid_early,compact,round,firm', 'Геліос', 'Середньоранній сорт. Технічна стиглість плодів настає через 100-116 днів після проростання. Рослина компактна, висотою 40-58 см. Плоди округлі, фіолетові, масою 150-200 г. М \'якоть біла, щільна, без гіркоти. Врожайність висока.', 'early,mid_early,compact,round,firm');
