INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (280, 2, 'Цибуля', 'Біла   королева. Чудовий середньостиглий сорт цибулі італійської селекції. Утворює красиві, великі цибулини, вагою 120-150 г, півострівного смаку. Зовнішня та внутрішня лусочки білосніжні, цибулини щільні, вирівняні, соковиті. Відмінний смак.', 'mid_maturing,juice,red,juicy,firm', 'Цибуля', 'Біла   королева. Чудовий середньостиглий сорт цибулі італійської селекції. Утворює красиві, великі цибулини, вагою 120-150 г, півострівного смаку. Зовнішня та внутрішня лусочки білосніжні, цибулини щільні, вирівняні, соковиті. Відмінний смак.', 'mid_maturing,juice,red,juicy,firm');
