INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (284, 2, 'Глобус', 'Гострий універсальний середньостиглий сорт. Вегетаційний період 92-94 дні. Зовнішні лусочки коричневі або світло-коричневі. Всередині ваги соковиті, білі. Кругла або подовжена кругла цибулина масою 75-90 г. Може вирощуватися з насіння в однорічній культурі, а також з посіву.', 'mid_maturing,juice,universal,brown,round,elongated,juicy', 'Глобус', 'Гострий універсальний середньостиглий сорт. Вегетаційний період 92-94 дні. Зовнішні лусочки коричневі або світло-коричневі. Всередині ваги соковиті, білі. Кругла або подовжена кругла цибулина масою 75-90 г. Може вирощуватися з насіння в однорічній культурі, а також з посіву.', 'mid_maturing,juice,universal,brown,round,elongated,juicy');
