INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (286, 2, 'Каратальского', 'Ранньостиглий, продуктивний. Вирощується в однорічній культурі з насіння. Цибулина щільна, округла. Сухі лусочки золотисто-жовті, соковиті – білі. Маса цибулини 50-120 г. Смак напівгострий. Зрілість перед збиранням врожаю становить 97%.Лежкість- 80%.', 'early_maturing,juice,yellow,round,juicy,firm,keeping,high_yield', 'Каратальского', 'Ранньостиглий, продуктивний. Вирощується в однорічній культурі з насіння. Цибулина щільна, округла. Сухі лусочки золотисто-жовті, соковиті – білі. Маса цибулини 50-120 г. Смак напівгострий. Зрілість перед збиранням врожаю становить 97%.Лежкість- 80%.', 'early_maturing,juice,yellow,round,juicy,firm,keeping,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (287, 2, 'Луганський', 'Луганський.піньостиглий , дуже врожайний сорт.Вирощуеться  в щорічній культурі з насіння. Смак проміжний між гострим та півгострим.цибулинка кругла, і овальна. Сухі лусочки жовті з коричневим відтінком, соковитим - білим. Маса цибулини 71-146 г.', 'late,juice,yellow,brown,round,oval,juicy,high_yield', 'Луганський', 'Луганський.піньостиглий , дуже врожайний сорт.Вирощуеться  в щорічній культурі з насіння. Смак проміжний між гострим та півгострим.цибулинка кругла, і овальна. Сухі лусочки жовті з коричневим відтінком, соковитим - білим. Маса цибулини 71-146 г.', 'late,juice,yellow,brown,round,oval,juicy,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (288, 2, 'Любчик', 'Один з найвідоміших сортів у світі. Раннє дозрівання, від посіву до дозрівання – 120 днів. Цибулини великі, щільні, видовжені. Вага цибулини 50, 0-94 г. Колір сухих лусочок жовто-коричневий. Смак пряний.', 'early_maturing,yellow,brown,round,flat_round,firm', 'Любчик', 'Один з найвідоміших сортів у світі. Раннє дозрівання, від посіву до дозрівання – 120 днів. Цибулини великі, щільні, видовжені. Вага цибулини 50, 0-94 г. Колір сухих лусочок жовто-коричневий. Смак пряний.', 'early_maturing,yellow,brown,round,flat_round,firm');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (289, 2, 'Марковський', 'Сорт середнье стиглий напівгострий,врожайний,  ідеально зберігається. Його можна вирощувати з насіння в щорічній культурі, а також із сівка Цибуля слід вирощувати на родючих, добре оброблених легких, що має нейтральну реакцію ґрунтах , маса120-130 г.', 'mid_maturing,keeping,high_yield', 'Марковський', 'Сорт середнье стиглий напівгострий,врожайний,  ідеально зберігається. Його можна вирощувати з насіння в щорічній культурі, а також із сівка Цибуля слід вирощувати на родючих, добре оброблених легких, що має нейтральну реакцію ґрунтах , маса120-130 г.', 'mid_maturing,keeping,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (290, 2, 'Ред барон', 'Ред Барон. Незважаючи на відносну ранньестиглість вона ідеально зберігається. Луковичка середнього розміру, вагою 50-120 г, щільний, привабливий червоний колір, який залишається на цибулині і не плямить ножа або компонент салату. Він має відмінний смак.', 'early_maturing,red,firm', 'Ред барон', 'Ред Барон. Незважаючи на відносну ранньестиглість вона ідеально зберігається. Луковичка середнього розміру, вагою 50-120 г, щільний, привабливий червоний колір, який залишається на цибулині і не плямить ножа або компонент салату. Він має відмінний смак.', 'early_maturing,red,firm');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (291, 2, 'Рубін', 'Смачний, скоростиглий, продуктивність висока. До технічної стиглості - 72-85 днів. Цибуля напівгостра округла, однонгніздова, вагою 60-80 г. Сухі лусочки рожево-фіолетові, соковиті, білі з рожевим епідермісом. Він добре дозріває у всіх зонах України.', 'juice,pink,round,juicy,high_yield', 'Рубін', 'Смачний, скоростиглий, продуктивність висока. До технічної стиглості - 72-85 днів. Цибуля напівгостра округла, однонгніздова, вагою 60-80 г. Сухі лусочки рожево-фіолетові, соковиті, білі з рожевим епідермісом. Він добре дозріває у всіх зонах України.', 'juice,pink,round,juicy,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (292, 2, 'Стригуновский', 'Ранньостиглий, продуктивний. Вирощується в однорічній культурі з насіння. Цибулина щільна, закруглена з легким пробігом вгору-вниз. Сухі лусочки жовті, соковиті – білі. Вага цибулини 45-80 г. Дозрілість до збору врожаю 97%. Постіль хороша, смак гострий.', 'early_maturing,juice,yellow,round,juicy,firm,keeping,high_yield', 'Стригуновский', 'Ранньостиглий, продуктивний. Вирощується в однорічній культурі з насіння. Цибулина щільна, закруглена з легким пробігом вгору-вниз. Сухі лусочки жовті, соковиті – білі. Вага цибулини 45-80 г. Дозрілість до збору врожаю 97%. Постіль хороша, смак гострий.', 'early_maturing,juice,yellow,round,juicy,firm,keeping,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (293, 2, 'Халцедон', 'Цибулина велика, кругла, золотистого кольору, має чудовий вигляд. Врожайність 700 центнерів з гектара, зберігається 12 місяців, смак високий.', 'round,marketable', 'Халцедон', 'Цибулина велика, кругла, золотистого кольору, має чудовий вигляд. Врожайність 700 центнерів з гектара, зберігається 12 місяців, смак високий.', 'round,marketable');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (294, 2, 'Чорнушка', 'Чернушка.Вирощування ріпчатого лука через сівок, отримання сівка за допомогою посіву насіння на перший рік, за допомогою цього методу  врожай їотримується на другий рік, посіяна у квітні після розморожування ґрунту на ширині 1 м, в канавки, виготовлені на відстані 10-12 см.Кількість посіву 9-10 г на 1 кв.', 'black', 'Чорнушка', 'Чернушка.Вирощування ріпчатого лука через сівок, отримання сівка за допомогою посіву насіння на перший рік, за допомогою цього методу  врожай їотримується на другий рік, посіяна у квітні після розморожування ґрунту на ширині 1 м, в канавки, виготовлені на відстані 10-12 см.Кількість посіву 9-10 г на 1 кв.', 'black');
