INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (298, 2, 'Шніт', 'Його цінують як рослину, яка дає вітамінну зелень цілий рік. Його листя ніжні, злегка гострі і ароматні. Багаторічна рослина, яка дає хороший урожай на одній ділянці протягом 5 років, швидко росте ранньою весною і дає велику зелену масу.', 'round', 'Шніт', 'Його цінують як рослину, яка дає вітамінну зелень цілий рік. Його листя ніжні, злегка гострі і ароматні. Багаторічна рослина, яка дає хороший урожай на одній ділянці протягом 5 років, швидко росте ранньою весною і дає велику зелену масу.', 'round');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (299, 2, 'Порей', 'Дворічна рослина. У перший рік утворює зелене листя і потовщене біле стебло (довжиною 15-60 см і діаметром 5-7 см), яке є основною їстівною частиною рослини.', '', 'Порей', 'Дворічна рослина. У перший рік утворює зелене листя і потовщене біле стебло (довжиною 15-60 см і діаметром 5-7 см), яке є основною їстівною частиною рослини.', '');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (300, 2, 'Батун', 'Багаторічна зимостійка рослина, яка використовується  На одному місці росте 5-7 років. Листя ніжні, гострі за смаком. Протягом вегетаційного періоду, коли він відростає, можна кілька разів зрізати листя. Світлолюбна рослина, не витримує затінення.', '', 'Батун', 'Багаторічна зимостійка рослина, яка використовується  На одному місці росте 5-7 років. Листя ніжні, гострі за смаком. Протягом вегетаційного періоду, коли він відростає, можна кілька разів зрізати листя. Світлолюбна рослина, не витримує затінення.', '');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (301, 2, 'Амстердамська', 'Ранньостиглі, придатні для вирощування під плівкою та для  ранньої ринкової торгівлі. Від масової розсади до технічної стиглості – 70-90 днів. Коренеплод довжиною 17-19 см, циліндричний, оранжевого кольору з високим вмістом каротину', 'early_maturing,orange,cylindrical', 'Амстердамська', 'Ранньостиглі, придатні для вирощування під плівкою та для  ранньої ринкової торгівлі. Від масової розсади до технічної стиглості – 70-90 днів. Коренеплод довжиною 17-19 см, циліндричний, оранжевого кольору з високим вмістом каротину', 'early_maturing,orange,cylindrical');
