INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (303, 2, 'Оленка', 'Сорт ранньостиглий, стиглість Ринкова стиглість настає на 47-49-й, технічна - на 85-100-й день після появи розсади. Урожайність 40-75 кг/м2. Коренеплод помаранчевий, зрізано-конічної форми, тупий, довжиною 10-12 см.', 'early,early_maturing,orange', 'Оленка', 'Сорт ранньостиглий, стиглість Ринкова стиглість настає на 47-49-й, технічна - на 85-100-й день після появи розсади. Урожайність 40-75 кг/м2. Коренеплод помаранчевий, зрізано-конічної форми, тупий, довжиною 10-12 см.', 'early,early_maturing,orange');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (304, 2, 'Болтекс', 'Покращенний різновид моркви шантанового типу середньопізнього дозрівання. Вегетаційний період від розсади до збору врожаю становить 120-125 днів. Коріння конічні, гладкі, насичено-оранжеві, довжиною 10-15 см, масою 200-350 г.', 'mid_late,late,orange', 'Болтекс', 'Покращенний різновид моркви шантанового типу середньопізнього дозрівання. Вегетаційний період від розсади до збору врожаю становить 120-125 днів. Коріння конічні, гладкі, насичено-оранжеві, довжиною 10-15 см, масою 200-350 г.', 'mid_late,late,orange');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (305, 2, 'Віта Лонга', 'Німеччина. Пізньесередній сорт період вегетації 115-130 днів. Коріння конічної форми, гладкі, рівномірні, темно-помаранчеві, мають високу товарність, довжиною 20-30 см, вагою до 350 г. Відмінний смак.', 'mid_late,late,orange', 'Віта Лонга', 'Німеччина. Пізньесередній сорт період вегетації 115-130 днів. Коріння конічної форми, гладкі, рівномірні, темно-помаранчеві, мають високу товарність, довжиною 20-30 см, вагою до 350 г. Відмінний смак.', 'mid_late,late,orange');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (306, 2, 'Вітамінна 6', 'Вітамінна 6.Середньостиглий сорт. Період від посіву до збирання становить 80-100 днів, а при вирощуванні на ринкову торгівлю відбувається на 67-75 днів. Коренева культура потовщена циліндричною, зі слабкою звуженням до основи довжиною 15 см, вагою 72-132 г, помаранчево-червоним.', 'mid_maturing,red,orange,cylindrical', 'Вітамінна 6', 'Вітамінна 6.Середньостиглий сорт. Період від посіву до збирання становить 80-100 днів, а при вирощуванні на ринкову торгівлю відбувається на 67-75 днів. Коренева культура потовщена циліндричною, зі слабкою звуженням до основи довжиною 15 см, вагою 72-132 г, помаранчево-червоним.', 'mid_maturing,red,orange,cylindrical');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (307, 2, 'Каротель', 'Сорт середньостиглий, період вегетації 100-110 днів від розсади до технічної стиглості. Підходить для ранньовесняного та передзимового посіву. Коренеплод інтенсивний помаранчевий, м \'якоть соковита і смачна. Використовуються для безпосереднього споживання, переробки, зберігання.', 'mid_maturing,juice,orange,juicy', 'Каротель', 'Сорт середньостиглий, період вегетації 100-110 днів від розсади до технічної стиглості. Підходить для ранньовесняного та передзимового посіву. Коренеплод інтенсивний помаранчевий, м \'якоть соковита і смачна. Використовуються для безпосереднього споживання, переробки, зберігання.', 'mid_maturing,juice,orange,juicy');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (308, 2, 'Корал', 'Польща Пізнє дозрівання, призначене для тривалого зберігання. Вегетаційний період від посіву до технічної стиглості 140-150 днів. Коріння темно-помаранчеві, циліндричні або злегка конічні, довжиною 18-20 см, вагою 140-160 г, рівномірні', 'late,orange,cylindrical', 'Корал', 'Польща Пізнє дозрівання, призначене для тривалого зберігання. Вегетаційний період від посіву до технічної стиглості 140-150 днів. Коріння темно-помаранчеві, циліндричні або злегка конічні, довжиною 18-20 см, вагою 140-160 г, рівномірні', 'late,orange,cylindrical');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (309, 2, 'Королева осені', 'Сорт пізньостиглий, високоврожайний, період вегетації 120-130 днів. Коренеплод циліндричний, тупий, оранжево-червоний, діаметром 5 см і довжиною 20-25 см. Вага коренеплоду до 220 г. М \'якоть і серцевина коренеплоду ніжна і соковита.', 'late,juice,red,orange,cylindrical,juicy,high_yield', 'Королева осені', 'Сорт пізньостиглий, високоврожайний, період вегетації 120-130 днів. Коренеплод циліндричний, тупий, оранжево-червоний, діаметром 5 см і довжиною 20-25 см. Вага коренеплоду до 220 г. М \'якоть і серцевина коренеплоду ніжна і соковита.', 'late,juice,red,orange,cylindrical,juicy,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (310, 2, 'Красавка.', 'Новий супервисоковрожайний сорт. Вегетаційний період 70-85 днів після появи розсади. Коренеплоди мають конічну форму, майже однаковий діаметр по всій довжині. Довжина коренеплоду 17-19 см. Колір м \'якоті та серцевини інтенсивний помаранчевий, що створює видимість відсутності серцевини.', 'early,orange,high_yield', 'Красавка.', 'Новий супервисоковрожайний сорт. Вегетаційний період 70-85 днів після появи розсади. Коренеплоди мають конічну форму, майже однаковий діаметр по всій довжині. Довжина коренеплоду 17-19 см. Колір м \'якоті та серцевини інтенсивний помаранчевий, що створює видимість відсутності серцевини.', 'early,orange,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (311, 2, 'Московська зимова', 'Високоврожайний середньопізній сорт. Один з кращих сортів для зимового зберігання. Коріння гладкі, витягнуті-конічні, довжиною 15-18 см, з  тупим кінцем, оранжево-червоні, практично без серцевини. Вага коренеплодів 130-150 г.', 'mid_late,late,red,orange,elongated,high_yield', 'Московська зимова', 'Високоврожайний середньопізній сорт. Один з кращих сортів для зимового зберігання. Коріння гладкі, витягнуті-конічні, довжиною 15-18 см, з  тупим кінцем, оранжево-червоні, практично без серцевини. Вага коренеплодів 130-150 г.', 'mid_late,late,red,orange,elongated,high_yield');
