INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (313, 2, 'Червона без серцевини', 'Червоний без серцевини, середньоранній сорт Коренеплід циліндричні, заокругленні, вагою 130-210г, довжина 19-21 см. М’якоть червоно-помаранчева, соковита, ніжна, солодка. Різноманітність відрізняється відмінними королівськими транспортними засобами Півня.', 'early,mid_early,juice,red,orange,cylindrical,sweet,juicy,keeping', 'Червона без серцевини', 'Червоний без серцевини, середньоранній сорт Коренеплід циліндричні, заокругленні, вагою 130-210г, довжина 19-21 см. М’якоть червоно-помаранчева, соковита, ніжна, солодка. Різноманітність відрізняється відмінними королівськими транспортними засобами Півня.', 'early,mid_early,juice,red,orange,cylindrical,sweet,juicy,keeping');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (314, 2, 'Ням ням', 'Ранній гарний сорт. Підходить для свіжого використання та для консервування. Коренеплоди великі, без серцевини, циліндричної форми, яскраво-червоного кольору, довжиною 15 см. Сорт дуже солодкий, з високим вмістом каротину,тому часто використовуетьс в пюре для дітей, легкий.', 'early,canning,fresh,red,cylindrical,sweet,keeping', 'Ням ням', 'Ранній гарний сорт. Підходить для свіжого використання та для консервування. Коренеплоди великі, без серцевини, циліндричної форми, яскраво-червоного кольору, довжиною 15 см. Сорт дуже солодкий, з високим вмістом каротину,тому часто використовуетьс в пюре для дітей, легкий.', 'early,canning,fresh,red,cylindrical,sweet,keeping');
