INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (316, 2, 'Цариця полів', 'Пізньостиглий сорт. Підходить для свіжого використання та для консервування. Коренеплоди великі, циліндричні, яскраво-оранжеві, довжиною 18-20 см, легкі. Підходить для зимового зберігання. Не вимогливий до грунту, не тріскається.', 'late,canning,fresh,orange,cylindrical,keeping', 'Цариця полів', 'Пізньостиглий сорт. Підходить для свіжого використання та для консервування. Коренеплоди великі, циліндричні, яскраво-оранжеві, довжиною 18-20 см, легкі. Підходить для зимового зберігання. Не вимогливий до грунту, не тріскається.', 'late,canning,fresh,orange,cylindrical,keeping');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (317, 2, 'Червона Бояриня', 'Новий, середньопізній сорт німецької селекції. Вегетаційний період 125-140 днів. Коренеплоди циліндричні, довжиною 19-22 см, оранжевого  кольору, відрізняються соковитістю та відмінним смаком. Чудовий сорт для споживання, переробки та зберігання свіжих продуктів.', 'mid_late,late,fresh,juice,red,orange,cylindrical,juicy', 'Червона Бояриня', 'Новий, середньопізній сорт німецької селекції. Вегетаційний період 125-140 днів. Коренеплоди циліндричні, довжиною 19-22 см, оранжевого  кольору, відрізняються соковитістю та відмінним смаком. Чудовий сорт для споживання, переробки та зберігання свіжих продуктів.', 'mid_late,late,fresh,juice,red,orange,cylindrical,juicy');
