INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (401, 2, 'Кавілі F1', 'Kavili F1. Для отримання ультраранських та масових продуктів використовується узбережжя гібридних кабачків. Він відрізняється довгим періодом плодоношення (близько 2 місяців). Плоди циліндричні, дуже прямі, біло-зелені з білою м’яковою, дуже однорідною, з ніжним смаком.', 'cylindrical', 'Кавілі F1', 'Kavili F1. Для отримання ультраранських та масових продуктів використовується узбережжя гібридних кабачків. Він відрізняється довгим періодом плодоношення (близько 2 місяців). Плоди циліндричні, дуже прямі, біло-зелені з білою м’яковою, дуже однорідною, з ніжним смаком.', 'cylindrical');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (402, 2, 'Світозар', 'Сорт Wrandesy, 41-50 днів проходять від масових саджанців до першої колекції фруктів. Рослина - це форма куща, без бічних пагонів. Сервіс Цуккіні. Плоди циліндричні, гладкі, жовті, довжиною 26-30 см, вагою 500-800 г.', 'early_maturing,yellow,cylindrical', 'Світозар', 'Сорт Wrandesy, 41-50 днів проходять від масових саджанців до першої колекції фруктів. Рослина - це форма куща, без бічних пагонів. Сервіс Цуккіні. Плоди циліндричні, гладкі, жовті, довжиною 26-30 см, вагою 500-800 г.', 'early_maturing,yellow,cylindrical');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (403, 2, 'Патисон Малахіт', 'Патіссон Малахіт. Wrandes, на плодоносах входить на 65 -й день. Рослина - форма куща. Плоди невеликі, нальоту зі закругленими краями, зеленими. Цінність вітаміну та дієтичного продукту.', 'early_maturing,round', 'Патисон Малахіт', 'Патіссон Малахіт. Wrandes, на плодоносах входить на 65 -й день. Рослина - форма куща. Плоди невеликі, нальоту зі закругленими краями, зеленими. Цінність вітаміну та дієтичного продукту.', 'early_maturing,round');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (404, 2, 'Патисон Помаранчевий', 'Патіссон помаранчевий. Робота, плодоношення входить на 65 днів. Рослина - форма куща. Плоди невеликі, нальоту з краївами округлої сорти, в технічній стиглості, світло-жовтого кольору в біологічно-помаранчевому. Цінність вітаміну та дієтичного продукту.', 'early,yellow,orange,round', 'Патисон Помаранчевий', 'Патіссон помаранчевий. Робота, плодоношення входить на 65 днів. Рослина - форма куща. Плоди невеликі, нальоту з краївами округлої сорти, в технічній стиглості, світло-жовтого кольору в біологічно-помаранчевому. Цінність вітаміну та дієтичного продукту.', 'early,yellow,orange,round');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (405, 2, 'Патисон Сашенька', 'Патіссон Саша. Новий сорт, невеликий -фрут, продуктивні, дошки, білі, в збереженні нагадує гриби.', 'canning,high_yield', 'Патисон Сашенька', 'Патіссон Саша. Новий сорт, невеликий -фрут, продуктивні, дошки, білі, в збереженні нагадує гриби.', 'canning,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (406, 2, 'Салат Верна', 'Салат правда. Сорт середньої (70-75 днів). Голови щільні, можуть досягти маси близько 1 кг. Розфарбовування готових продуктів яскраво -зелений. Листя добре відокремлені один від одного. Салат соковитий, хрусткий.', 'mid_maturing,juice,juicy,firm,crunchy', 'Салат Верна', 'Салат правда. Сорт середньої (70-75 днів). Голови щільні, можуть досягти маси близько 1 кг. Розфарбовування готових продуктів яскраво -зелений. Листя добре відокремлені один від одного. Салат соковитий, хрусткий.', 'mid_maturing,juice,juicy,firm,crunchy');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (407, 2, 'Латук', 'Ранньостиглий салат, утворює кучеряву голову з красивим блискучим листям. Консистенція листа хрустка, щільна. Смак дуже хороший. Високі товарні якості, має тривалий термін зберігання.', 'early_maturing,firm,crunchy', 'Латук', 'Ранньостиглий салат, утворює кучеряву голову з красивим блискучим листям. Консистенція листа хрустка, щільна. Смак дуже хороший. Високі товарні якості, має тривалий термін зберігання.', 'early_maturing,firm,crunchy');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (408, 2, 'Салат Лоло Біонда', 'Салат Лоло Біонд. Салат -салат з Wrandes, призначений для вирощування з весни до пізньої осені. Від повних пагонів до технічної стиглості 40-50 днів. Листя кучеряві, світло -зелені розмальовки, м\'ясисті та ніжні за смаком.', 'early_maturing,late,meaty', 'Салат Лоло Біонда', 'Салат Лоло Біонд. Салат -салат з Wrandes, призначений для вирощування з весни до пізньої осені. Від повних пагонів до технічної стиглості 40-50 днів. Листя кучеряві, світло -зелені розмальовки, м\'ясисті та ніжні за смаком.', 'early_maturing,late,meaty');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (409, 2, 'Салат Лоло Росса', 'Салат Лоло Росс. Салат з раннього стиглого листя. Від повних пагонів до технічної стиглості 40-50 днів. Листя кучеряві, світло -зелене забарвлення з широким рожевим краєм. Його культивують у відкритому та захищеному ґрунті.', 'early_maturing,pink,open_field', 'Салат Лоло Росса', 'Салат Лоло Росс. Салат з раннього стиглого листя. Від повних пагонів до технічної стиглості 40-50 днів. Листя кучеряві, світло -зелене забарвлення з широким рожевим краєм. Його культивують у відкритому та захищеному ґрунті.', 'early_maturing,pink,open_field');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (410, 2, 'Одеський кучерявець', 'Ранньостиглий, високоврожайний (до 5 кг/кв. м) сорт, багатий вітамінами та мінеральними солями. Рослина утворює фігурне листя, без вмісту гіркоти. Культура холодостійка.', 'early_maturing,cold_tolerant,high_yield', 'Одеський кучерявець', 'Ранньостиглий, високоврожайний (до 5 кг/кв. м) сорт, багатий вітамінами та мінеральними солями. Рослина утворює фігурне листя, без вмісту гіркоти. Культура холодостійка.', 'early_maturing,cold_tolerant,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (411, 2, 'Палітра', 'Суміш семи найпопулярніших видів салату, 1000 насінин. Розрахований на тривалий період збору врожаю через різницю в зрілості сортів.', '', 'Палітра', 'Суміш семи найпопулярніших видів салату, 1000 насінин. Розрахований на тривалий період збору врожаю через різницю в зрілості сортів.', '');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (412, 2, 'Салат Рукола', 'Салат руколою. Розпусна багаторічна рослина. Листя збирають у товстій базальній розетці. Свіжі молоді листя використовуються з гострим гострим смаком та ароматом волоських горіхів, з яких готують салати �� страви з сиром.', 'fresh', 'Салат Рукола', 'Салат руколою. Розпусна багаторічна рослина. Листя збирають у товстій базальній розетці. Свіжі молоді листя використовуються з гострим гострим смаком та ароматом волоських горіхів, з яких готують салати �� страви з сиром.', 'fresh');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (413, 2, 'Салат Сніжинка', 'Ранньостиглий сорт листя, для вирощування у відкритому ґрунті та в плівкових укриттях. Вегетаційний період від розсади до технічної стиглості - 20-25 днів. Утворює компактну розетку діаметром 20-25 см.', 'early_maturing,compact,open_field,greenhouse', 'Салат Сніжинка', 'Ранньостиглий сорт листя, для вирощування у відкритому ґрунті та в плівкових укриттях. Вегетаційний період від розсади до технічної стиглості - 20-25 днів. Утворює компактну розетку діаметром 20-25 см.', 'early_maturing,compact,open_field,greenhouse');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (414, 2, 'Салат', 'Айсберг. Wrandesy, високий -різноманітний кочовий салат для весняного та осіннього вирощування у відкритому ґрунті та під фільмом. Вейг -сезон від розсади до збирання - 58-65 днів. Утворює великі голови.', 'early_maturing,open_field,high_yield', 'Салат', 'Айсберг. Wrandesy, високий -різноманітний кочовий салат для весняного та осіннього вирощування у відкритому ґрунті та під фільмом. Вейг -сезон від розсади до збирання - 58-65 днів. Утворює великі голови.', 'early_maturing,open_field,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (415, 2, 'Салат Тайфун', 'Листовий салат з хрустким соковитим листям. Розетка листя висотою 20 см, діаметром 27 см. Лист великий, зелений, міцно-волокнистий. Вага рослини 200-220 г, врожайність 3,5 кг/м2. Смак відмінний.', 'juice,juicy,crunchy', 'Салат Тайфун', 'Листовий салат з хрустким соковитим листям. Розетка листя висотою 20 см, діаметром 27 см. Лист великий, зелений, міцно-волокнистий. Вага рослини 200-220 г, врожайність 3,5 кг/м2. Смак відмінний.', 'juice,juicy,crunchy');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (416, 2, 'Салат Мізунь', 'Це японська листова капуста, невибаглива у вирощуванні, з ніжним листям, широко використовується в стравах як прикраса з особливим смаком.', '', 'Салат Мізунь', 'Це японська листова капуста, невибаглива у вирощуванні, з ніжним листям, широко використовується в стравах як прикраса з особливим смаком.', '');
