INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (420, 2, 'Щавель широколистий', 'Весна широка. Ранні Нед, від розсади до економічної придатності 40-45 днів. Стійкий, зимовий -харді. Лист витягнутий-ядерний, зелений. Це рекомендується для вирощування у відкритій ґрунті протягом весни, літа та датів сублекції Севи.', 'early,early_maturing,elongated,open_field', 'Щавель широколистий', 'Весна широка. Ранні Нед, від розсади до економічної придатності 40-45 днів. Стійкий, зимовий -харді. Лист витягнутий-ядерний, зелений. Це рекомендується для вирощування у відкритій ґрунті протягом весни, літа та датів сублекції Севи.', 'early,early_maturing,elongated,open_field');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (421, 2, 'Базелік фіолетовий', 'Рослина не переносить низьких температур. Розмножується розсадою або посівом у відкритий грунт. Розсаду висівають за 1,5-2 місяці до висадки в грунт, на глибину 1,5-2 см.', 'open_field', 'Базелік фіолетовий', 'Рослина не переносить низьких температур. Розмножується розсадою або посівом у відкритий грунт. Розсаду висівають за 1,5-2 місяці до висадки в грунт, на глибину 1,5-2 см.', 'open_field');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (422, 2, 'Базилік Єреванський', 'Цей передчасний сорт з багатим вмістом ефірних олій стане вишуканою приправою до страв. Має приємний запах перцю і чаю, свіжий є цінним джерелом каротину.', 'fresh', 'Базилік Єреванський', 'Цей передчасний сорт з багатим вмістом ефірних олій стане вишуканою приправою до страв. Має приємний запах перцю і чаю, свіжий є цінним джерелом каротину.', 'fresh');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (423, 2, 'Базилік Мамонт', 'Василь Маммонт. Салатор, ранній -остальний сорт (30-40 днів) з темпо-25-40 див. Росліна Форма Компактна Нуад з великою ясністю-зеленими листівками (як латунний салат ") я можу ароматизувати".', 'early,early_maturing,compact,salad', 'Базилік Мамонт', 'Василь Маммонт. Салатор, ранній -остальний сорт (30-40 днів) з темпо-25-40 див. Росліна Форма Компактна Нуад з великою ясністю-зеленими листівками (як латунний салат ") я можу ароматизувати".', 'early,early_maturing,compact,salad');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (424, 2, 'Грибна трава', 'Однорічна трав \'яниста рослина з родини бобових. Використовується як пряність, що надає грибам неповторний смак і аромат. Розмножується насінням, яке висівають у другій половині квітня.', '', 'Грибна трава', 'Однорічна трав \'яниста рослина з родини бобових. Використовується як пряність, що надає грибам неповторний смак і аромат. Розмножується насінням, яке висівають у другій половині квітня.', '');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (425, 2, 'Естрагон / тархун /', 'Багаторічна рослина, вирощена на одному місці протягом 10 років. Листя ніжні, злегка гіркі, з анісовим смаком, мають сильний аромат. Містить ефірні олії, вітамін С, каротин, рутин.', '', 'Естрагон / тархун /', 'Багаторічна рослина, вирощена на одному місці протягом 10 років. Листя ніжні, злегка гіркі, з анісовим смаком, мають сильний аромат. Містить ефірні олії, вітамін С, каротин, рутин.', '');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (426, 2, 'Кмин', 'Пряноароматична рослина, що утворює розетку листя в перший рік, в другий - стебло і насіння. Молоде листя або насіння використовують в їжу, з ними готують супи і тушкують м \'ясо, додають до смаку хлібобулочних виробів.', '', 'Кмин', 'Пряноароматична рослина, що утворює розетку листя в перший рік, в другий - стебло і насіння. Молоде листя або насіння використовують в їжу, з ними готують супи і тушкують м \'ясо, додають до смаку хлібобулочних виробів.', '');
