INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (431, 2, 'Огіркова трава Бораго', 'Мало молодої огіркової трави, які мають лікарські властивості, використовуються як рослина салату.  Листя збирають, поки не з’явиться стовбур квітка.', 'salad', 'Огіркова трава Бораго', 'Мало молодої огіркової трави, які мають лікарські властивості, використовуються як рослина салату.  Листя збирають, поки не з’явиться стовбур квітка.', 'salad');
