INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (213, 1, 'Брюссельская', 'Двулетнее растение. В первый год образует стебель высотой 40-60 см. В пазухах листьев развиваются небольшие кочанчики величиной от грецкого ореха до куриного яйца.  Используют в отваренном, жаренном и тушенном видах, как гарнир для вторых блюд, для супа.', '', 'Брюссельская', 'Двулетнее растение. В первый год образует стебель высотой 40-60 см. В пазухах листьев развиваются небольшие кочанчики величиной от грецкого ореха до куриного яйца.  Используют в отваренном, жаренном и тушенном видах, как гарнир для вторых блюд, для супа.', '');
