INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (215, 1, 'Брокколи', 'Разновидность спаржевой капусты. Образует рыхлую головку темно-зеленого цвета. Отличается высокой питательной ценностью. Используют для приготовления первых и вторых блюд, замораживают, консервируют.', 'canning', 'Брокколи', 'Разновидность спаржевой капусты. Образует рыхлую головку темно-зеленого цвета. Отличается высокой питательной ценностью. Используют для приготовления первых и вторых блюд, замораживают, консервируют.', 'canning');
