INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (389, 1, 'Белоплодный', 'Раннеспелый, очень высокоурожайный сорт. Растение кустовое, с большим количеством женских цветков, что обеспечивает большую отдачу урожая. Плод цилиндрический, с небольшим сужением к плодоножке, беловатый, массой 0, 6-0, 9 кг. Мякоть белая или светло-кремовая, средней плотности.', 'early_maturing,cylindrical,firm,high_yield', 'Белоплодный', 'Раннеспелый, очень высокоурожайный сорт. Растение кустовое, с большим количеством женских цветков, что обеспечивает большую отдачу урожая. Плод цилиндрический, с небольшим сужением к плодоножке, беловатый, массой 0, 6-0, 9 кг. Мякоть белая или светло-кремовая, средней плотности.', 'early_maturing,cylindrical,firm,high_yield');
