INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (391, 1, 'Грибовский 37', 'Сорт скороспелый, урожайный. Плод цилиндрический, гладкий, бледно-зеленый. Выращивают в открытом и защищенном грунте. Урожайная, болезнестойкая культура. Для получения ранней продукции высаживают 20-25 апреля в укрытие.', 'cylindrical,disease_resistant,open_field,high_yield', 'Грибовский 37', 'Сорт скороспелый, урожайный. Плод цилиндрический, гладкий, бледно-зеленый. Выращивают в открытом и защищенном грунте. Урожайная, болезнестойкая культура. Для получения ранней продукции высаживают 20-25 апреля в укрытие.', 'cylindrical,disease_resistant,open_field,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (392, 1, 'Зебра', 'Сорт раннеспелый, в плодоношение вступает на 38-й день. Растение кустовое, компактное, плетей мало, главный стебель короткий. Плод цилиндрический, массой 0, 5 кг, светло-зеленый, рисунок – широкие продольные темно-зеленые полосы. Мякоть белая, хрустящая, плотная, нежная, сочная.', 'early_maturing,compact,juice,cylindrical,juicy,firm,crunchy', 'Зебра', 'Сорт раннеспелый, в плодоношение вступает на 38-й день. Растение кустовое, компактное, плетей мало, главный стебель короткий. Плод цилиндрический, массой 0, 5 кг, светло-зеленый, рисунок – широкие продольные темно-зеленые полосы. Мякоть белая, хрустящая, плотная, нежная, сочная.', 'early_maturing,compact,juice,cylindrical,juicy,firm,crunchy');
