INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (408, 1, 'Салат Лоло Бионда', 'Раннеспелый листовой салат предназначенный для выращивания с весны до поздней осени. От полных всходов до технической спелости 40-50 дней. Листья курчавые, светло-зеленой окраски, мясистые и нежные на вкус.', 'early_maturing,late,meaty', 'Салат Лоло Бионда', 'Раннеспелый листовой салат предназначенный для выращивания с весны до поздней осени. От полных всходов до технической спелости 40-50 дней. Листья курчавые, светло-зеленой окраски, мясистые и нежные на вкус.', 'early_maturing,late,meaty');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (409, 1, 'Салат Лоло Росса', 'Раннеспелый листовой салат. От полных всходов до технической спелости 40-50 дней. Листья курчавые, светло-зеленой окраски с широким розовым краем. Возделывается в открытом и защищенном грунте.', 'early_maturing,pink,open_field', 'Салат Лоло Росса', 'Раннеспелый листовой салат. От полных всходов до технической спелости 40-50 дней. Листья курчавые, светло-зеленой окраски с широким розовым краем. Возделывается в открытом и защищенном грунте.', 'early_maturing,pink,open_field');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (410, 1, 'Одесский кучерявец', 'Раннеспелый, высокоурожайный (до 5 кг/кв. м) сорт, богатый витаминами и минеральными солями. Растение формирует курчавые листья, без содержания горечи, . культура холодостойкая.', 'early_maturing,cold_tolerant,high_yield', 'Одесский кучерявец', 'Раннеспелый, высокоурожайный (до 5 кг/кв. м) сорт, богатый витаминами и минеральными солями. Растение формирует курчавые листья, без содержания горечи, . культура холодостойкая.', 'early_maturing,cold_tolerant,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (411, 1, 'Палитра', 'Смесь семи самых популярных видов салата, 1000семян. Предназначенных для длительного срока сбора в виду разности сроков созревания сортов.', '', 'Палитра', 'Смесь семи самых популярных видов салата, 1000семян. Предназначенных для длительного срока сбора в виду разности сроков созревания сортов.', '');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (412, 1, 'Салат Руккола', 'Теплолюбивое многолетнее растение. Листья собраны в густую прикорневую розетку. В пищу используют свежие молодые листья с пряным острым вкусом и ароматом ореха, из которых готовят салаты  �� блюда с сыром.', 'fresh', 'Салат Руккола', 'Теплолюбивое многолетнее растение. Листья собраны в густую прикорневую розетку. В пищу используют свежие молодые листья с пряным острым вкусом и ароматом ореха, из которых готовят салаты  �� блюда с сыром.', 'fresh');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (413, 1, 'Салат Снежинка', 'Раннеспелый листовой сорт, для выращивания в открытом грунте и в пленочных укрытиях. Вегетационный период от всходов до технической спелости 20-25 дней. Образует компактную розетку, диаметром 20-25 см.', 'early_maturing,compact,open_field,greenhouse', 'Салат Снежинка', 'Раннеспелый листовой сорт, для выращивания в открытом грунте и в пленочных укрытиях. Вегетационный период от всходов до технической спелости 20-25 дней. Образует компактную розетку, диаметром 20-25 см.', 'early_maturing,compact,open_field,greenhouse');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (414, 1, 'Салат', 'Айсберг. Раннеспелый, высокоурожайный сорт кочанного салата для весеннего и осеннего выращивания в открытом грунте и под пленкой. Вегетационный период от всходов до сбора урожая - 58-65 дней. Формирует крупные кочаны.', 'early_maturing,open_field,high_yield', 'Салат', 'Айсберг. Раннеспелый, высокоурожайный сорт кочанного салата для весеннего и осеннего выращивания в открытом грунте и под пленкой. Вегетационный период от всходов до сбора урожая - 58-65 дней. Формирует крупные кочаны.', 'early_maturing,open_field,high_yield');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (415, 1, 'Салат Тайфун', 'Листовой салат с хрустящими сочными листьями. Розетка листьев высотой 20 см, диаметром 27 см. Лист крупный, зеленый, сильноволокнистый. Масса растения 200-220 гр, урожайность 3, 5 кг/м2. Вкус отличный.', 'juice,juicy,crunchy', 'Салат Тайфун', 'Листовой салат с хрустящими сочными листьями. Розетка листьев высотой 20 см, диаметром 27 см. Лист крупный, зеленый, сильноволокнистый. Масса растения 200-220 гр, урожайность 3, 5 кг/м2. Вкус отличный.', 'juice,juicy,crunchy');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (416, 1, 'Салат Мизуна', 'Это японская листовая капуста, неприхотливая в выращивании, с нежными листьями широко применяемая в блюдах как украшение с особенным вкусом.', '', 'Салат Мизуна', 'Это японская листовая капуста, неприхотливая в выращивании, с нежными листьями широко применяемая в блюдах как украшение с особенным вкусом.', '');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (417, 1, 'Салат', 'Каталогна. Среднеспелый листовой салат, с приподнятой розеткой массой 90-100гр, итальянский сорт, предназначен для бутербродов.', 'mid_maturing', 'Салат', 'Каталогна. Среднеспелый листовой салат, с приподнятой розеткой массой 90-100гр, итальянский сорт, предназначен для бутербродов.', 'mid_maturing');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (418, 1, 'Валерианнела салатная', 'Однолетнее растение, хорошо развита прикорневая розетка, влаголюбива, листья сладкие, используются до цветения.', 'salad,sweet', 'Валерианнела салатная', 'Однолетнее растение, хорошо развита прикорневая розетка, влаголюбива, листья сладкие, используются до цветения.', 'salad,sweet');
