INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (420, 1, 'Щавель широколистый', 'Раннеспелый, от всходов до хозяйственной годности 40-45 дней. Устойчив, зимостойкий. Лист удлиненно-яйцевидный, зеленый. Рекомендуется для выращивания в открытом грунте при весеннем, летнем и подзимнем сроках сева.', 'early,early_maturing,elongated,open_field', 'Щавель широколистый', 'Раннеспелый, от всходов до хозяйственной годности 40-45 дней. Устойчив, зимостойкий. Лист удлиненно-яйцевидный, зеленый. Рекомендуется для выращивания в открытом грунте при весеннем, летнем и подзимнем сроках сева.', 'early,early_maturing,elongated,open_field');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (421, 1, 'Базелик фиолетовый', 'Растение, не выносит низких температур. Размножают рассадой или посевом в открытый грунт. На рассаду высевают за 1, 5-2 месяца до высадки в грунт, на глубину 1, 5-2см.', 'open_field', 'Базелик фиолетовый', 'Растение, не выносит низких температур. Размножают рассадой или посевом в открытый грунт. На рассаду высевают за 1, 5-2 месяца до высадки в грунт, на глубину 1, 5-2см.', 'open_field');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (422, 1, 'Базилик Ереванский', 'Этот скороспелый сорт с богатым содержанием эфирных масел станет изысканной приправой для блюд. Имеет приятный запах душистого перца и чая, в свежем виде является ценным источником каротина.', 'fresh', 'Базилик Ереванский', 'Этот скороспелый сорт с богатым содержанием эфирных масел станет изысканной приправой для блюд. Имеет приятный запах душистого перца и чая, в свежем виде является ценным источником каротина.', 'fresh');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (423, 1, 'Базилик Мамонт', 'Салатний, ранньостиглий сорт (30-40 днів) висотою 25-40 см. Рослина формує компактний кущик з великими яскраво-зеленими листками (по типу салату Латук") і має дуже чудовий аромат. "', 'early,early_maturing,compact,salad', 'Базилик Мамонт', 'Салатний, ранньостиглий сорт (30-40 днів) висотою 25-40 см. Рослина формує компактний кущик з великими яскраво-зеленими листками (по типу салату Латук") і має дуже чудовий аромат. "', 'early,early_maturing,compact,salad');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (424, 1, 'Грибная трава', 'Однолетнее травянистое растение из семейства бобовых. Используется как пряно��ть, которая придает неповторимый вкус и аромат грибов. Размножается семенами, которые высевают во второй половине апреля.', '', 'Грибная трава', 'Однолетнее травянистое растение из семейства бобовых. Используется как пряно��ть, которая придает неповторимый вкус и аромат грибов. Размножается семенами, которые высевают во второй половине апреля.', '');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (425, 1, 'Эстрагон/ тархун/', 'Многолетнее растение, выращивают на одном месте 10 лет. Листья нежные, слегка горьковатые, с анисовым привкусом, имеют сильный аромат. Содержат эфирные масла, витамин С, каротин, рутин.', '', 'Эстрагон/ тархун/', 'Многолетнее растение, выращивают на одном месте 10 лет. Листья нежные, слегка горьковатые, с анисовым привкусом, имеют сильный аромат. Содержат эфирные масла, витамин С, каротин, рутин.', '');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (426, 1, 'Тмин', 'Пряно-вкусовое растение, образующее в первый год розетку листьев, во второй - стебель и семена. В пищу используют молодые листья или семена, с ними варят супы и тушат мясо, добавляют для ароматизации выпечки.', '', 'Тмин', 'Пряно-вкусовое растение, образующее в первый год розетку листьев, во второй - стебель и семена. В пищу используют молодые листья или семена, с ними варят супы и тушат мясо, добавляют для ароматизации выпечки.', '');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (427, 1, 'Кинза', 'Однолетнее растение с пряными листьями и семенами. Свежую зелень используют для приготовления салатов,  мясных, рыбных, овощных блюд.  Семена используют для ароматизации при хлебопечении, изготовлении колбас, тушении мяса.', 'fresh', 'Кинза', 'Однолетнее растение с пряными листьями и семенами. Свежую зелень используют для приготовления салатов,  мясных, рыбных, овощных блюд.  Семена используют для ароматизации при хлебопечении, изготовлении колбас, тушении мяса.', 'fresh');
