INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (429, 1, 'Укроп Грибовский', 'От полных всходов до цветения 62-67 дней. Розетка полураскидистая, средней величины. Лист темно-зеленый, со слабым восковым налетом, ароматичность высокая. С меньшей требовательностью.
