INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (431, 1, 'Огуречная трава Бораго', 'Листя молодой огуречной травы, которые имеют лекарственные свойства, используют как салатное растение.  Листья собирают до появления цветочного стебля.', 'salad', 'Огуречная трава Бораго', 'Листя молодой огуречной травы, которые имеют лекарственные свойства, используют как салатное растение.  Листья собирают до появления цветочного стебля.', 'salad');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (432, 1, 'Пастернак Петр', 'Пряное растение. Вкус сладковатый. Ароматичность высокая. В пищу (в домашней кулинарии, при консервации) используют корнеплоды и листья в молодом возрасте.', 'canning,paste,sweet', 'Пастернак Петр', 'Пряное растение. Вкус сладковатый. Ароматичность высокая. В пищу (в домашней кулинарии, при консервации) используют корнеплоды и листья в молодом возрасте.', 'canning,paste,sweet');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (433, 1, 'Петрушка корневая', 'Корневой скороспелый сорт, вегетационный период 65-75 дней. Холодостойкий, хорошо зимует в открытом грунте. Устойчив к грибковым заболеваниям. Для получения хороших крупных корнеплодов, во время роста зелень не срезайте - этим вы ослабите растение.', 'open_field', 'Петрушка корневая', 'Корневой скороспелый сорт, вегетационный период 65-75 дней. Холодостойкий, хорошо зимует в открытом грунте. Устойчив к грибковым заболеваниям. Для получения хороших крупных корнеплодов, во время роста зелень не срезайте - этим вы ослабите растение.', 'open_field');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (434, 1, 'Петрушка Гигантела  листовая', 'Скороспелый сорт, вегетационный период 65-75 дней. Образует крупную розетку(50-100 листьев), формирует ранний и высокий  урожай сочной зеленой массы и крупный корень. Сорт холодостойкий, хорошо зимует.', 'early,early_maturing,juice,juicy,cold_tolerant', 'Петрушка Гигантела  листовая', 'Скороспелый сорт, вегетационный период 65-75 дней. Образует крупную розетку(50-100 листьев), формирует ранний и высокий  урожай сочной зеленой массы и крупный корень. Сорт холодостойкий, хорошо зимует.', 'early,early_maturing,juice,juicy,cold_tolerant');
INSERT INTO oc_product_description (product_id, language_id, name, description, tag, meta_title, meta_description, meta_keyword) VALUES (435, 1, 'Петрушка Гигантела', 'Листовой скороспелый сорт, вегетационный период 65-75 дней. Образует крупную розетку(50-100 листьев), формирует ранний и высокий  урожай сочной зеленой массы и крупный корень.', 'early,early_maturing,juice,juicy', 'Петрушка Гигантела', 'Листовой скороспелый сорт, вегетационный период 65-75 дней. Образует крупную розетку(50-100 листьев), формирует ранний и высокий  урожай сочной зеленой массы и крупный корень.', 'early,early_maturing,juice,juicy');
